 package selinum;

public class Arithmetic_operater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x =10;
int y =5;
int z;
 z = (x+y);  // + addition 
 System.out.println(z);
 z=x-y; // - subtraction
 System.out.println(z);
 z=x*y; //* multiply
 System.out.println(z);
 z=x/y; // / divided
 System.out.println(z);
 z=x%y; // % modulus
 System.out.println(z);
 System.out.println("10+5="+(10+5));
 System.out.println("10-5="+(10-5));
 System.out.println("10*5="+(10*5));
 System.out.println("10/5="+(10/5));
 System.out.println("10%5="+(10%5));
 int a=8;
 int b=16;
 int c;
 c=(a+b);
 System.out.println(c);
 
 
	}

}
